import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import io from "socket.io-client";
import axios from "axios";
import styles from "../ChatRoom.module.css"; // Import CSS module

const socket = io("http://localhost:5000");

function ChatRoom() {
  const { roomId } = useParams();
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const username = "User" + Math.floor(Math.random() * 1000);

  useEffect(() => {
    socket.emit("joinRoom", { username, room: roomId });

    axios.get(`http://localhost:5001/api/chat/rooms/${roomId}/messages`).then((res) => setMessages(res.data));

    socket.on("message", (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => {
      socket.emit("leaveRoom");
      socket.off();
    };
  }, [roomId]);

  const sendMessage = () => {
    if (!message) return;
    socket.emit("sendMessage", { room: roomId, sender: username, content: message });
    setMessage("");
  };

  return (
    <div className={styles.chatContainer}>
      <h2>Chat Room</h2>
      <div className={styles.chatMessages}>
        {messages.map((msg, index) => (
          <p key={index}><strong>{msg.sender}:</strong> {msg.content}</p>
        ))}
      </div>
      <input className={styles.chatInput} type="text" placeholder="Type a message" value={message} onChange={(e) => setMessage(e.target.value)} />
      <button className={styles.sendBtn} onClick={sendMessage}>Send</button>
    </div>
  );
}

export default ChatRoom;
