import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Dashboard() {
  const [rooms, setRooms] = useState([]);
  const [newRoom, setNewRoom] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:5000/api/chat/rooms").then((res) => setRooms(res.data));
  }, []);

  const createRoom = async () => {
    if (!newRoom) return;
    await axios.post("http://localhost:5000/api/chat/create-room", { name: newRoom });
    setNewRoom("");
    window.location.reload();
  };

  return (
    <div>
      <h2>Chat Rooms</h2>
      <input type="text" placeholder="New Room Name" value={newRoom} onChange={(e) => setNewRoom(e.target.value)} />
      <button onClick={createRoom}>Create Room</button>
      <ul>
        {rooms.map((room) => (
          <li key={room._id} onClick={() => navigate(`/chat/${room._id}`)}>{room.name}</li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
